import React from 'react';
import { Link } from 'react-router-dom';

const Search =({changePages, search, dataSe, Reads, WantToRead, CurrentlyReading})=>
{
    return (
        <div className="search-books">
        <div className="search-books-bar">
          <Link to="/">
            <button className="close-search" onClick={changePages}>Close</button>
          </Link>
          <div className="search-books-input-wrapper">
            {
            /*
              NOTES: The search from BooksAPI is limited to a particular set of search terms.
              You can find these search terms here:
              https://github.com/udacity/reactnd-project-myreads-starter/blob/master/SEARCH_TERMS.md

              However, remember that the BooksAPI.search method DOES search by title or author. So, don't worry if
              you don't find a specific author or title. Every search is limited by search terms.
            */}
            <input type="text" onChange={(e)=>search(e)} placeholder="Search by title or author"/>

          </div>
        </div>
        <div className="search-books-results">
          <ol className="books-grid">
            {WantToRead.filter((data)=>data.author.toLowerCase().includes("l")).map((crData,crindex)=>(
            <li>
              {crData.author}
            </li>))}
          
          </ol>
        </div>
      </div>
    );
}

export default Search;
