import React from 'react'
// import * as BooksAPI from './BooksAPI'
import './App.css';
//import Router packages
import { BrowserRouter, Route } from 'react-router-dom';
// Let me import my External Components
import Checks from './components/checking';
import Search from './components/searching';
import CReading from './components/CurrentlyReading';
import WTRead from './components/WantToRead';
import Read from './components/read';
import BottomButton from './components/navigator';

class BooksApp extends React.Component {
  state = {
    /**
     * TODO: Instead of using this state variable to keep track of which page
     * we're on, use the URL in the browser's address bar. This will ensure that
     * users can use the browser's back and forward buttons to navigate between
     * pages, as well as provide a good URL they can bookmark and share.
     */
    searched: [],
    Reads: [{title:"The Hobbit",
            author:"J.R.R. Tolkien"},
            {title:"Oh, the Places You'll Go!",
            author:"Seuss"},
            {title:"The Adventures of Tom Sawyer",
            author:"Mark Twain"}],
    
    CurrentlyReading : [{title: "To Kill a Mockingbird",
            author: "Harper Lee"},
            {title: "Ender's Game",
            author: "Orson Scott Card"}],

    WantToRead : [{title: "1779",
                  author: "David McCullough"},
                  {title: "Harry Potter and the Sorcerer's Stone",
                  author: "J.K. Rowling"}],

    check: "",
    pages: "/"
  }

  getReads()
  {
    return this.state.Reads;
  }

  render() {
    let element=<span>
        <ul>
        {
          this.state.WantToRead.map((readsData,rindex)=>
            (
              <li key={rindex}>
                {readsData.title} by {readsData.author}
              </li>
            ))
        }
        </ul>

    </span>;
    return (
      <div className="app">
        <Route path="/search">
           <Search changePages={()=>this.setState({pages:"/"})} />
        </Route>
        <Route  exact path="/">
           <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
              <div>

                <CReading />

                <WTRead ReadsData={this.state.WantToRead} />

                <Read ReadsData={this.state.Reads}/>

              </div>
            </div>
            <BottomButton changePages={()=>
            this.setState({pages: "/search"})}/>
          </div>
        </Route>
      </div>
    )
  }
}

export default BooksApp
